            {/* <select value={"en"} onChange={() => changeLanguage(lng)}>
              {languages.map((languages) => (
                <option key={languages.value} value={languages.value}>
                  {languages.text}
                </option>
              ))}
            </select> */}
            {/* <select id="lang" onChange={() => changeLanguage()}>
              <option value="en">English</option>
              <option value="mkd">Macedonian</option>
            </select> */}
            {/* <button type="button" onClick={() => changeLanguage("mkd")}>
              mkd
            </button>
