import { useRef } from "react";

export function useOnDraw() {
  const canvasRef = useRef(null);

  function setCanvasRef(ref) {
    if (!ref) return;
    canvasRef.current = ref;
    console.log(canvasRef);
  }

  return setCanvasRef;
}
