import React from "react";

const Background = () => {
  const menu = document.getElementById("menu");

  Array.from(document.getElementsByClassName("menu-item")).forEach(
    (item, index) => {
      item.onmouseover = () => {
        menu.dataset.activeIndex = index;
      };
    }
  );
};

export default Background;
