import React, { useRef } from "react";
import Backwards from "./sections/Backwards.js";

export const Canvas = () => {
  const canvas = document.querySelector("#canvas");
  const ctx = canvas.getContext("2d");
  ctx.lineCap = "round";
  let drawing = false;

  //Default Canvas Settings
  canvas.width = 800;
  canvas.height = 400;

  ctx.fillStyle = "white";
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.save();
  ctx.mozImageSmoothingEnabled = false;
  ctx.imageSmoothingEnabled = false;

  const pen = document.querySelector("#pen");
  let penSelected = true;
  const eraser = document.querySelector("#eraser");
  let eraserSelected = false;

  pen.addEventListener("click", () => {
    penSelected = true;
    eraserSelected = false;
    document.querySelector(".brush-size").style.visibility = "visible";
    document.querySelector(".eraser-size").style.visibility = "hidden";
    console.log("pen:" + penSelected);
    console.log("eraser :" + eraserSelected);
  });

  eraser.addEventListener("click", () => {
    eraserSelected = true;
    penSelected = false;
    document.querySelector(".brush-size").style.visibility = "hidden";
    document.querySelector(".eraser-size").style.visibility = "visible";
    console.log("pen:" + penSelected);
    console.log("eraser:" + eraserSelected);
  });

  //PC
  canvas.addEventListener("mousedown", StartDrawing, false);
  canvas.addEventListener("mousemove", Drawing, false);
  canvas.addEventListener("mouseup", StopDrawing, false);
  canvas.addEventListener("mouseout", StopDrawing, false);
  //Mobile
  canvas.addEventListener("touchstart", StartDrawing, false);
  canvas.addEventListener(
    "touchmove",
    function () {
      var touch = e.touches[0];
      let mouseEvent = new MouseEvent("mousemove", {
        clientX: touch.clientX,
        clientY: touch.clientY,
      });
      canvas.dispatchEvent(mouseEvent);
      // Drawing();
    },
    false
  );
  canvas.addEventListener("touchend", StopDrawing, false);

  function StartDrawing(e) {
    drawing = true;
    if (penSelected) {
      //Pen
      ctx.beginPath();
      ctx.moveTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
      ctx.strokeStyle = currentColor;
      ctx.lineWidth = currentBrushSize;
    } else if (eraserSelected) {
      //Eraser
      ctx.beginPath();
      ctx.moveTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
      ctx.strokeStyle = "white";
      ctx.lineWidth = currentEraserSize;
    }
    ctx.lineCap = "round";
    ctx.lineJoin = "round";
    ctx.stroke();
    e.preventDefault();
  }

  function Drawing(e) {
    if (drawing) {
      if (penSelected) {
        ctx.lineTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
        ctx.moveTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
        ctx.strokeStyle = currentColor;
        ctx.lineWidth = currentBrushSize;
      } else if (eraserSelected) {
        ctx.lineTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
        ctx.moveTo(e.clientX - canvas.offsetLeft, e.clientY - canvas.offsetTop);
        ctx.strokeStyle = "white";
        ctx.lineWidth = currentEraserSize;
      }
      ctx.lineCap = "round";
      ctx.lineJoin = "round";
      ctx.stroke();
      e.preventDefault();
    }
  }

  function StopDrawing(e) {
    if (drawing) {
      drawing = false;

      ctx.stroke();
      ctx.closePath(); //ends the path/drawing
    }
    e.preventDefault();
  }

  // Pen Color

  let color = document.querySelector("#color");
  let currentColor = "black";

  if (color) {
    color.addEventListener("change", (event) => {
      currentColor = color.value;
    });
  }

  // Pen Size

  let brushSize = document.querySelector("#pen-size");
  let currentBrushSize = brushSize.value;

  if (brushSize) {
    brushSize.addEventListener("change", (event) => {
      currentBrushSize = brushSize.value;
    });
  }

  //Eraser Size

  let eraserSize = document.querySelector("#eraser-size");
  let currentEraserSize = eraserSize.value;

  if (eraserSize) {
    eraserSize.addEventListener("change", (event) => {
      currentEraserSize = eraserSize.value;
    });
  }

  // Download the Canvas

  const downloadCanvas = document.querySelector(".download");

  downloadCanvas.addEventListener("click", imageDownload);
  downloadCanvas.addEventListener("touch", imageDownload);

  function imageDownload() {
    const imageLink = document.createElement("a"); //creates a link which is needed to download the file
    imageLink.download = "download.png";
    imageLink.href = canvas.toDataURL("image/png", 1);
    imageLink.click(); //it's triggered when the button is clicked
  }

  //Clear Canvas

  const clearCanvas = document.querySelector(".clear-canvas");

  clearCanvas.addEventListener("click", Clear);
  clearCanvas.addEventListener("touch", Clear);

  function Clear() {
    ctx.fillStyle = "white";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
  }

  // window.onload = window.onresize = function () {
  //   if (screen.width <= 800) {
  //     //     canvas.width = window.innerWidth * 0.8;
  //     //     canvas.height = window.innerHeight * 0.8;
  //     canvas.width = window.outerWidth * 0.8;
  //   } else {
  //     canvas.width = 800;
  //   }
  // };

  return (
    <div className="canvas-background">
      <div className="tool-menu">
        <input className="checkbox" type="checkbox" name="" id="" />

        <svg className="menu" viewBox="0 0 24 24">
          <path d="M4 6h16v2H4zm0 5h16v2H4zm0 5h16v2H4z"></path>
        </svg>

        <div className="tools">
          <div className="tool">
            <svg viewBox="0 0 24 24" id="pen">
              <path d="M7.061 22c1.523 0 2.84-.543 3.91-1.613 1.123-1.123 1.707-2.854 1.551-4.494l8.564-8.564a3.123 3.123 0 0 0-.002-4.414c-1.178-1.18-3.234-1.18-4.412 0l-8.884 8.884c-1.913.169-3.807 1.521-3.807 3.919 0 .303.021.588.042.86.08 1.031.109 1.418-1.471 2.208a1.001 1.001 0 0 0-.122 1.717C2.52 20.563 4.623 22 7.061 22c-.001 0-.001 0 0 0zM18.086 4.328a1.144 1.144 0 0 1 1.586.002 1.12 1.12 0 0 1 0 1.584L12 13.586 10.414 12l7.672-7.672zM6.018 16.423c-.018-.224-.037-.458-.037-.706 0-1.545 1.445-1.953 2.21-1.953.356 0 .699.073.964.206.945.475 1.26 1.293 1.357 1.896.177 1.09-.217 2.368-.956 3.107C8.865 19.664 8.049 20 7.061 20H7.06c-.75 0-1.479-.196-2.074-.427 1.082-.973 1.121-1.989 1.032-3.15z"></path>
            </svg>

            <div className="brush-size">
              <input type="range" min="1" max="100" value="5" id="pen-size" />
            </div>
          </div>
          <div className="tool">
            <svg viewBox="0 0 24 24" id="eraser">
              <path d="M12.48 3 7.73 7.75 3 12.59a2 2 0 0 0 0 2.82l4.3 4.3A1 1 0 0 0 8 20h12v-2h-7l7.22-7.22a2 2 0 0 0 0-2.83L15.31 3a2 2 0 0 0-2.83 0zM8.41 18l-4-4 4.75-4.84.74-.75 4.95 4.95-4.56 4.56-.07.08z"></path>
            </svg>

            <div className="eraser-size">
              <input
                type="range"
                min="1"
                max="100"
                value="5"
                id="eraser-size"
              />
            </div>
          </div>
          <div className="tool">
            <input type="color" value="#000000" id="color" />
          </div>
          <div className="tool">
            <svg viewBox="0 0 24 24" className="download">
              <path d="m12 16 4-5h-3V4h-2v7H8z"></path>
              <path d="M20 18H4v-7H2v7c0 1.103.897 2 2 2h16c1.103 0 2-.897 2-2v-7h-2v7z"></path>
            </svg>
          </div>
          <div className="tool">
            <svg viewBox="0 0 24 24" className="clear-canvas">
              <path d="M19.937 8.68c-.011-.032-.02-.063-.033-.094a.997.997 0 0 0-.196-.293l-6-6a.997.997 0 0 0-.293-.196c-.03-.014-.062-.022-.094-.033a.991.991 0 0 0-.259-.051C13.04 2.011 13.021 2 13 2H6c-1.103 0-2 .897-2 2v16c0 1.103.897 2 2 2h12c1.103 0 2-.897 2-2V9c0-.021-.011-.04-.013-.062a.99.99 0 0 0-.05-.258zM16.586 8H14V5.414L16.586 8zM6 20V4h6v5a1 1 0 0 0 1 1h5l.002 10H6z"></path>
            </svg>
          </div>
        </div>
      </div>

      <Backwards />

      <canvas
        id="canvas"
        useRef={(c) => (this.context = c.getContext("2d"))}
      ></canvas>
    </div>
  );
};

export default Canvas;
