import React, { useEffect, useRef, useState, useContext } from "react";

// import { Paint } from "./Paint";
import Backwards from "./sections/Backwards.js";
import Tools from "./sections/Tools.js";

export const Paint = () => {
  //Canvas and Context References
  const canvasRef = useRef(null);
  const contextRef = useRef(null);

  const [isDrawing, setIsDrawing] = useState(false);

  const [color, setColor] = useState("");

  useEffect(() => {
    //Canvas
    const canvas = canvasRef.current;
    canvas.width = 800;
    canvas.height = 400;

    //Context
    const ctx = canvas.getContext("2d");
    ctx.mozImageSmoothingEnabled = false;
    ctx.imageSmoothingEnabled = false;
    ctx.fillStyle = "white";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    ctx.lineCap = "round";

    let currentColor = color;
    ctx.strokeStyle = color.current;
    ctx.lineWidth = 5;
    contextRef.current = ctx;
  }, []);

  const startDrawing = ({ nativeEvent }) => {
    setIsDrawing(true);

    const { offsetX, offsetY } = nativeEvent;
    // console.log(offsetX + " " + offsetY);

    contextRef.current.beginPath();
    contextRef.current.moveTo(offsetX, offsetY);
    contextRef.current.stroke();
    // nativeEvent.preventDefault();
  };

  const Drawing = ({ nativeEvent }) => {
    if (!isDrawing) {
      return;
    } else {
      // console.log(offsetX + " " + offsetY);

      if (nativeEvent.type == "touchmove") {
        const { offsetX } = nativeEvent.touches[0];
        const { offsetY } = nativeEvent.touches[0];

        console.log(offsetX + " " + offsetY);
        contextRef.current.lineTo(
          nativeEvent.touches[0].clientX,
          nativeEvent.touches[0].clientY
        );
      } else {
        const { offsetX, offsetY } = nativeEvent;

        contextRef.current.lineTo(offsetX, offsetY);
      }
      contextRef.current.stroke();
      // nativeEvent.preventDefault();
    }
  };

  const stopDrawing = () => {
    setIsDrawing(false);
    contextRef.current.closePath();
  };

  // function mobileDrawing(event) {
  //   var touch = event.touches[0];
  //   let mouseEvent = new MouseEvent("mousemove", {
  //     clientX: touch.clientX,
  //     clientY: touch.clientY,
  //   });
  //   canvasRef.current.dispatchEvent(mouseEvent);
  //   // Drawing();
  // }

  const setToDraw = () => {
    contextRef.current.globalCompositeOperation = "source-over";
  };

  const setToErase = () => {
    contextRef.current.globalCompositeOperation = "destination-out";
  };

  const downloadDrawing = (event) => {
    //Selects the target of the event the current state of the Canvas.
    //Then downloads it as an image and names it "Sketch.png"
    let link = event.currentTarget;
    let image = canvasRef.current.toDataURL("image/png");
    link.setAttribute("download", "SketchNDraw.png");
    link.setAttribute("href", image);
  };

  function clearCanvas(e) {
    contextRef.current.fillStyle = "white";
    contextRef.current.fillRect(
      0,
      0,
      canvasRef.current.width,
      canvasRef.current.height
    );
  }

  return (
    <>
      <Tools />
      <Backwards />

      <canvas
        id="canvas"
        onMouseDown={startDrawing}
        onMouseMove={Drawing}
        onMouseUp={stopDrawing}
        onMouseLeave={stopDrawing}
        onTouchStart={startDrawing}
        onTouchMove={Drawing}
        onTouchEnd={stopDrawing}
        ref={canvasRef}
      />

      <a onClick={downloadDrawing}>Download</a>
      <input
        type="color"
        id="color"
        onChange={(e) => setColor(e.target.value)}
      />

      <button onClick={clearCanvas}>Clear</button>
    </>
  );
};

export default Paint;
