import React from "react";
import { useTranslation } from "react-i18next";

import Backwards from "./sections/Backwards.js";

export const Settings = () => {
  const { t, i18n } = useTranslation();

  // const languages = [
  //   { value: "en", text: "English" },
  //   { value: "mkd", text: "Macedonian" },
  // ];

  const changeLanguage = (lng) => {
    i18n.changeLanguage(lng);
  };

  return (
    <div id="settings">
      <Backwards />

      <div className="settings-items">
        <div className="settings-title">
          <h1>Settings</h1>
        </div>

        <div className="settings-options">
          {/* <div className="settings-option">
            <h3>Color Theme</h3>
            <select id="theme">
              <option selected>Default Theme</option>
              <option>White Theme</option>
              <option>Black Theme</option>
            </select>
          </div> */}
          <div className="settings-option">
            <h3>{t("Language")}</h3>
            {/* <select value={"en"} onChange={() => changeLanguage(lng)}>
              {languages.map((languages) => (
                <option key={languages.value} value={languages.value}>
                  {languages.text}
                </option>
              ))}
            </select> */}
            <select
              id="lang"
              value={i18n.changeLanguage()}
              onChange={() => i18n.changeLanguage()}
            >
              <option value="en">English</option>
              <option value="mkd">Macedonian</option>
            </select>
            {/* <button type="button" onClick={() => changeLanguage("mkd")}>
              mkd
            </button>
            <button type="button" onClick={() => changeLanguage("en")}>
              en
            </button> */}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
